import express from 'express';
import cors from 'cors';
import bodyParser from 'body-parser';

import login from './controllers/authController';
import jsonPatch from './controllers/jsonPatchController';
import Imagethumbnail from './controllers/thumbnailController';
import logMonitor from './controllers/logMonitorController';

import { verifyToken } from './middlewares/token';
import logger from './middlewares/logger';

const app = express();
app.use(cors());


app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false }));

app.post('/v1/login', login);

app.patch('/v1/json-patch', verifyToken, logger, jsonPatch);

app.post('/v1/image_thumbnail', verifyToken, logger, Imagethumbnail);

app.get('/v1/logs', verifyToken, logger, logMonitor);

export default app;

import { createToken } from '../middlewares/token';

const login = (req, res) => {
  const { username, password } = req.body;

  if (!username) {
    return res.status(400).json({
      status: 'error',
      message: 'no username provided'
    });
  }

  if (!password) {
    return res.status(400).json({
      status: 'error',
      message: 'please enter a password'
    });
  }

  const token = createToken({ username, password });
  return res.status(200).json({
    status: 'success',
    token
  });
};

export default login;

import jwt from 'jsonwebtoken';

export const createToken = (data) => jwt.sign(data, 'itsABeautifulToken', { expiresIn: '1h' });

export const verifyToken = (req, res, next) => {
  if (!req.headers.authorization) {
    return res.status(403).json({
      status: 'error',
      message: 'No token found'
    });
  }
  const token = req.headers.authorization.split(' ')[1];

  return jwt.verify(token, 'itsABeautifulToken', (error, user) => {
    if (error) {
      return res.status(401).json({
        status: 'error',
        message: 'token is invalid'
      });
    }
    req.user = user;
    return next();
  });
};

import * as jsonpatch from 'fast-json-patch';


const jsonPatch = (req, res) => {
  const { jsonObject, jsonPatchObject } = req.body;
  if (!jsonObject) {
    return res.status(400).json({
      status: 'error',
      message: 'no json object found'
    });
  }
  if (!jsonPatchObject) {
    return res.status(400).json({
      status: 'error',
      message: 'no jsonPatchObject object found'
    });
  }
  const newFile = jsonpatch.applyPatch(jsonObject, jsonPatchObject).newDocument;
  return res.status(200).json({
    status: 'success',
    jsonObject: newFile
  });
};

export default jsonPatch;

import imageThumbnail from 'image-thumbnail';

const Imagethumbnail = async (req, res) => {
  const { imageUrl } = req.body;
  if (!imageUrl) {
    return res.status(404).json({
      status: "error",
      message: "No image url found"
    });
  }
  const imageRegex = /\.(jpeg|jpg|gif|png)$/;
  if (imageRegex.test(imageUrl) === false) {
    return res.status(403).json({
      status: 'error',
      message: 'invalid image url'
    });
  }
  const options = { width: 50, length: 50 };
  const thumbnail = await imageThumbnail({ uri: imageUrl }, options);
  return res.status(200).json({
    status: "success",
    thumbnail
  });
};

export default Imagethumbnail;

import fs from 'fs';
import path from 'path';

const logMonitor = (re, res) => {
  res.set('content-type', 'text/plain');
  const data = fs.readFileSync(path.join(process.cwd(), '/src/requests.log'));
  return res.status(200).send(data);
};

export default logMonitor;

import fs from 'fs';
import path from 'path';

const logger = (req, res, next) => {
  const time = new Date().getMilliseconds();
  const reqPath = req.originalUrl;
  res.on('finish', () => {
    const t = new Date().getMilliseconds();
    const timeDiff = t - time;
    const data = `\n${req.method}\t${reqPath}\t${res.statusCode}\t${timeDiff}ms`;
    fs.appendFileSync(path.join(process.cwd(), '/src/requests.log'), data);
  });
  next();
};

export default logger;

